package main

import (
	"bytes"
	"encoding/json"
	"fmt"
	"github.com/getkin/kin-openapi/openapi2"
	"github.com/getkin/kin-openapi/openapi2conv"
	"io/ioutil"
	"os"
)

// main This simple script will take the swagger 2 specs generated by grafana and convert them into openapi 3
// saving them as new json file to be able lo load and show
// The first parameter, if present, will be the input file
func main() {
	outFile := "public/openapi3.json"
	inFile := "api-merged.json"

	if args := os.Args[1:]; len(args) > 0 && args[0] != "" {
		inFile = args[0]
	}

	fmt.Printf("Reading swagger 2 file %s\n", inFile)
	byt, err := ioutil.ReadFile(inFile)
	if err != nil {
		panic(err)
	}

	var doc2 openapi2.T
	if err = json.Unmarshal(byt, &doc2); err != nil {
		panic(err)
	}

	doc3, err := openapi2conv.ToV3(&doc2)
	if err != nil {
		panic(err)
	}

	j3, err := doc3.MarshalJSON()
	if err != nil {
		panic(err)
	}

	var out bytes.Buffer
	err = json.Indent(&out, j3, "", "\t")
	if err != nil {
		panic(err)
	}

	if err = ioutil.WriteFile(outFile, out.Bytes(), 0644); err != nil {
		panic(err)
	}
	fmt.Printf("OpenAPI specs generated in file %s\n", outFile)
}
